//sayaç hg
client.on("guildMemberAdd", async member => {
  let sayı = await db.fetch(`sayaç_${member.guild.id}`)
  let kanal = await db.fetch(`sayaçK_${member.guild.id}`) 
  if(!sayı) return
  if(!kanal) return
  client.channels.get(kanal).send(`:loudspeaker: :inbox_tray: ${member} adlı üye sunucuya girdi **${sayı}** kişi olamamıza **${sayı - member.guild.members.size}** üye kaldı! Şuan **${member.guild.memberCount}** kişiyiz`)
})
client.on("guildMemberRemove", async member => {
  let sayı = await db.fetch(`sayaç_${member.guild.id}`)
  let kanal = await db.fetch(`sayaçK_${member.guild.id}`)
  if(!sayı) return
  if(!kanal) return
  client.channels.get(kanal).send(`:loudspeaker: :outbox_tray: ${member} adlı üye sunucudan çıktı **${sayı}** kişi olamamıza **${sayı - member.guild.members.size}** üye kaldı! Şuan **${member.guild.memberCount}** kişiyiz`)
})


///kanal sılme koruması
client.on("channelDelete", async channel => {
  let guild = channel.guild;
  const logs = await channel.guild.fetchAuditLogs({ type: 'CHANNEL_DELETE' })
  let member = guild.members.get(logs.entries.first().executor.id);
  if(!member) return;
  channel.clone(channel.name, true, true, "Kanal silme koruması sistemi").then(async klon => {
    await klon.setParent(channel.parent);
    await klon.setPosition(channel.position);
  })
})





//caps engel XDDDDDDDDDDDDDDDDDDD
client.on("message", async msg => {
  if(msg.channel.type === "dm") return
  if(msg.author.bot) return;  
  if(msg.content.length < 4) return
  if(!db.fetch(`capslock_${msg.guild.id}`)) return
  let caps = msg.content.toUpperCase();
  if(msg.content == caps) {
    if(msg.member.hasPermission("BAN_MEMBERS")) return
    let yashinu = msg.mentions.users.first() || msg.mentions.channels.first() || msg.mentions.roles.first();
    if(!yashinu && !msg.content.includes('@everyone') && !msg.content.includes('@here')) {
      msg.delete()
      return msg.channel.sendEmbed(new Discord.RichEmbed().setAuthor(client.user.username, client.user.avatarURL).setColor('RANDOM').setDescription(`${msg.author} Fazla büyük harf kullanmamalısın!`)).then(m => m.delete(5000))
    }
  }
  
  ////MSG GUNCELLEME
});
